function makeLoadingBar(curPrecent, loadingBarOptions) {
 const { height = 200, type, text } = loadingBarOptions || {};
 return `
<div class="container container-flex" style="display: flex;height: ${height}px;flex-direction: column;justify-content: center; width:100%">
 ${text ? `<h6 style="text-align: center;">${text}</h6>` : ""}
 <div class="progress">
 <div class="progress-bar progress-bar-striped progress-bar-animated ${type ? `bg-${type}` : ""}" role="progressbar" aria-valuenow="${curPrecent}" aria-valuemin="0" aria-valuemax="100" style="width: ${curPrecent}%"></div>
 </div>
</div>`;
}

/**
 * Returns a list of repositories. Errors if no repositories are found.
 * @param {RepositoryApiClient} apiClient 
 * @returns {Promise<Repository[]>}
 * @preserve
 */
const getRepositories = async (apiClient) => {
 const resolvedApiClient = apiClient ?? await LFForm.getLaserficheAPIClient("Default");
 const repositories = await resolvedApiClient.repositoriesClient.listRepositories({});
 if (!repositories.value || repositories.value.length === 0) {
 throw new Error("No repositories found");
 }
 return repositories.value;
};

const updateTableRows = async (tableFieldId, rowCount) => {
 const currentTableFieldValues = LFForm.getFieldValues(tableFieldId);
 const curRowCount = currentTableFieldValues.length;
 if (curRowCount < rowCount) {
 await LFForm.addRow(tableFieldId, rowCount - curRowCount);
 } else if (curRowCount > rowCount) {
 await LFForm.deleteRow(
 tableFieldId,
 ...Array.from(Array(curRowCount - rowCount)).map(
 (_, i) => curRowCount - i - 1
 )
 );
 }
};

const defaultSearchOptions = {
 pollInterval: 250,
 maxPollAttempts: 20,
 maxPageSize: 1e3
};
const searchAsync = async (apiClient, repositoryId, searchCommand, searchOptions) => {
 const { pollInterval, fuzzyFactor, fuzzyType, maxPageSize, maxPollAttempts } = {
 ...defaultSearchOptions,
 ...searchOptions
 };
 const openTasks = await apiClient.tasksClient.listTasks({ repositoryId });
 const openTaskIds = openTasks.value?.map((task) => task.id) ?? [];
 if (openTaskIds?.length && openTaskIds.length > 1) {
 console.warn("Cancelling previous search task");
 await apiClient.tasksClient.cancelTasks({
 repositoryId,
 taskIds: openTaskIds.filter((t) => t !== void 0).slice(1)
 }).catch();
 }
 const startTask = await apiClient.searchesClient.startSearchEntry({
 repositoryId,
 request: {
 searchCommand,
 fuzzyFactor,
 fuzzyType
 }
 });
 const { taskId } = startTask;
 if (taskId === void 0) return null;
 const finalTaskResponse = await new Promise(
 (resolve, reject) => {
 let awaitingInterval = false;
 let currentPollCount = 0;
 const timeout = setInterval(async () => {
 console.log(currentPollCount);
 if (awaitingInterval) return;
 if (currentPollCount >= maxPollAttempts) {
 clearInterval(timeout);
 reject("Search timed out");
 return;
 }
 currentPollCount++;
 awaitingInterval = true;
 const taskResponse = await apiClient.tasksClient.listTasks({
 repositoryId,
 taskIds: [taskId]
 });
 if (taskResponse?.value?.[0].status === "Completed") {
 clearInterval(timeout);
 resolve(taskResponse.value[0].result);
 return;
 }
 if (taskResponse?.value?.[0].status === "Failed") {
 clearInterval(timeout);
 reject(
 new Error(
 taskResponse.value[0].errors?.[0]?.title ?? "Unknown search error"
 )
 );
 return;
 }
 if (taskResponse?.value?.[0].status === "Cancelled") {
 clearInterval(timeout);
 resolve(void 0);
 return;
 }
 }, pollInterval);
 }
 );
 if (!finalTaskResponse) {
 return null;
 }
 const searchResponse = await apiClient.searchesClient.listSearchResults({
 repositoryId,
 taskId,
 prefer: `odata.maxpagesize=${maxPageSize}`
 });
 return searchResponse;
};
const fillTableWithSearchResults = async (tableFieldId, results, valueMap) => {
 if (!results?.value?.length) return;
 const fieldValues = Object.keys(valueMap).reduce((acc, fieldId) => {
 acc[fieldId] = [];
 return acc;
 }, {});
 for (const result of results.value) {
 for (const fieldId of Object.keys(valueMap)) {
 fieldValues[fieldId].push(valueMap[Number(fieldId)](result));
 }
 }
 await updateTableRows(tableFieldId, results.value.length);
 return Promise.allSettled(
 Object.entries(fieldValues).map(
 ([fieldId, values]) => LFForm.setFieldValues({ fieldId: Number(fieldId) }, values)
 )
 );
};

/**
 * The form fields used in this form.
 * @preserve
 */
const formFields = {
 withinFolder: { fieldId: 6 },
 dispositionType: { fieldId: 10 },
 recordRange: { fieldId: 11 },
 findFrom: { fieldId: 13 },
 findTo: { fieldId: 12 },
 searchButton: { fieldId: 9 },
 entryTable: { fieldId: 1 },
 entryId: { fieldId: 2 },
 entryName: { fieldId: 3 },
 entryType: { fieldId: 5 },
 entryLastModified: { fieldId: 4 }
};
/**
 * @param {string} value Any field that needs to be escaped when building the search syntax
 * @returns {string}
 * @preserve
 */
const escapeSearchSyntax = (value) => value.replaceAll('"', '\\"').replaceAll("\\", "\\\\");
/**
 * Builds and validates the search syntax based on the form fields.
 * Update this with your own logic to build the search syntax.
 * @returns {string} The search syntax
 * @throws {Error} If the search syntax is invalid
 * @preserve
 **/
const parseSearchCommand = () => {
 const withinFolder = LFForm.getFieldValues(
 formFields.withinFolder
 );
 const dispositionType = LFForm.getFieldValues(
 formFields.dispositionType
 );
 const dateRange = LFForm.getFieldValues(
 formFields.recordRange
 );
 const useDateRange = dateRange === "Eligible from";
 const findFrom = LFForm.getFieldValues(formFields.findFrom);
 const findTo = LFForm.getFieldValues(formFields.findTo);
 const searchCommand = [];
 if (withinFolder !== "") {
 searchCommand.push(escapeSearchSyntax(`({LF:LOOKIN="${withinFolder}"})`));
 }
 if (dispositionType.value.length == 0)
 throw new Error("Please select at least one disposition type");
 const fromDate = findFrom.dateTimeObj ? new Date(findFrom.dateTimeObj) : null;
 const fromDateString = fromDate ? `${fromDate.getFullYear()}/${fromDate.getMonth() + 1}/${fromDate.getDate()}` : null;
 const toDate = findTo.dateTimeObj ? new Date(findTo.dateTimeObj) : null;
 const toDateString = toDate ? `${toDate.getFullYear()}/${toDate.getMonth() + 1}/${toDate.getDate()}` : null;
 const dispositionTypeSearch = dispositionType.value.map((value) => {
 let dispositionSearch = `{LFRM:DispEligibilityAction="${value}"`;
 if (useDateRange && fromDateString) {
 dispositionSearch += `, EligibilityDate >= "${fromDateString}"`;
 }
 if (useDateRange && toDateString) {
 dispositionSearch += `, EligibilityDate <= "${toDateString}"`;
 }
 dispositionSearch += "}";
 return dispositionSearch;
 });
 searchCommand.push(`(${dispositionTypeSearch.join(" | ")})`);
 return searchCommand.join(" & ");
};
const main = async () => {
 await fullFieldHtml(
 formFields.entryTable,
 "textAbove",
 "Enter search criteria and press search to continue",
 "background-color: white;"
 );
 const apiClient = await LFForm.getLaserficheAPIClient(
 "Default"
 );
 const repository = await getRepositories(apiClient);
 if (!repository || !repository.length || !repository[0].id) {
 return;
 }
 const repositoryId = repository[0].id;
 window.handleSearchClick = async () => {
 try {
 const searchCommand = parseSearchCommand();
 console.log(searchCommand);
 await fullFieldHtml(
 formFields.entryTable,
 "textAbove",
 makeLoadingBar(100, { text: "Searching..." }),
 "background-color: white;"
 );
 const searchResults = await searchAsync(
 apiClient,
 repositoryId,
 searchCommand
 );
 if (!searchResults) {
 await fullFieldHtml(
 formFields.entryTable,
 "textAbove",
 "No results found",
 "background-color: white;"
 );
 return;
 }
 await fillTableWithSearchResults(formFields.entryTable, searchResults, {
 [formFields.entryId.fieldId]: (entry) => entry.id,
 [formFields.entryName.fieldId]: (entry) => entry.name,
 [formFields.entryType.fieldId]: (entry) => entry.entryType,
 [formFields.entryLastModified.fieldId]: (entry) => entry.lastModifiedTime ? {
 dateStr: entry.lastModifiedTime.toLocaleDateString()
 } : { dateStr: "" }
 });
 await fullFieldHtml(formFields.entryTable, "textAbove", null);
 } catch (e) {
 const message = e && "message" in e ? e.message : "An error occurred";
 await fullFieldHtml(
 formFields.entryTable,
 "textAbove",
 message,
 "background-color: white;"
 );
 }
 };
 await LFForm.changeFieldSettings(formFields.searchButton, {
 content: `<button class="btn btn-secondary float-right" onclick="handleSearchClick()">Search</button>`
 });
};
main().catch(console.error);
